import styled from 'styled-components';

export const InputContainer = styled.div`
  width: 100%;
  height: 75px;
  background-color: #ffffff;
  display: flex;
  align-items: center;
  justify-content: flex-end;
  font-size: 24px;
  font-family: 'Roboto';

  input {
    width: calc(100% - 400px); /* Reduz um pouco a largura para evitar overflow e corresponder ao tamanho do botão */
    height: 65px; /* Ajusta a altura para evitar o excesso de espaço */
    background-color: #f2f2f2; /* Cor de fundo um pouco mais clara */
    border: none;
    padding: 0 10px;
    font-size: 24px;
    font-family: 'Roboto';
    color: #333333; /* Cor do texto mais escura */
    transition: background-color 0.3s;

    &:focus {
      outline: none;
      background-color: #e5e5e5; /* Cor de fundo um pouco mais clara quando em foco */
    }
  }
`;
